options.handler:

function (button) {
    var message = Ext.userLanguage == "de"
        ? "Um diese Funktion zu verwenden muss das Plugin aktiviert und die Seite einmalig aktualisiert werden. Jetzt aktualisieren?"
        : "To use this functionality the plugin must be activated and the page needs to be refreshed once. Refresh now?"

    if (Shopware.apps.WirecardElasticEngineExtendOrder) {
        Ext.create('Shopware.apps.WirecardElasticEngineExtendOrder.view.GeneralInformationWindow').show();
    } else {
        if (confirm(message)) {
            window.location.reload();
        }
    }
}


function (button) {
    var message = Ext.userLanguage == "de"
        ? "Um diese Funktion zu verwenden muss das Plugin aktiviert und die Seite einmalig aktualisiert werden. Jetzt aktualisieren?"
        : "To use this functionality the plugin must be activated and the page needs to be refreshed once. Refresh now?"

    if (typeof wirecardeeChatOpen !== "undefined") {
        wirecardeeChatOpen();
    } else {
        if (confirm(message)) {
            window.location.reload();
        }
    }
}


